const Chat = ({location}) => {
    const [name, setname] = useState('');
    const [room, setroom] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    // const username = localStorage.getItem("username");
    // const chatroom = props.match.params.name
    const ENDPOINT = "localhost:5000"
    useEffect(()=>{
        const { username, chatroom } = queryString.parse(location.search);
        socket = io(ENDPOINT)
        setname(username)
        setroom(chatroom)
        socket.emit("join",{name:username,room:chatroom},()=>{
            // this is executed when callback is called in server
        })

        return ()=>{
            socket.emit("disconnect")
            socket.off()

        }


    },[ENDPOINT,location.search])

   useEffect(()=>{
       socket.on("message",(message)=>{
           setMessages([...messages, message])
       })
   },[messages]);

   //function for sending messsages
   const sendMessage = (event) => {
    event.preventDefault();

    if(message) {

      socket.emit('sendMessage', message, () => setMessage(''));
    }
  }
  console.log(message,messages)




    return(
        <div className ="outerContainer">
            <div className = "container">
                <input
                value = {message}
                onChange = {(event)=> setMessage(event.target.value)}
                onKeyPress = {(event)=>event.key === 'Enter'? sendMessage(event) : null}
                />
            </div>
        </div>
    )
}
 export default Chat;
